mapper.select:
select t.id,t.traceTime,t.traceDetails,t.traceResult,t.remarks,t.customerId,t.inputUserId,t.type,c.id as c_id,c.name as c_name
from traceRecord as t inner join customer as c on t.customerId=c.id


        c.name like concat('%',#{keyword},'%')


        and (t.traceTime between #{startTime} and #{endTime})


        and t.type = #{type}


LIMIT #{start}, #{pageSize}
